require((exports, module) => {

    const values = Windows.Storage.ApplicationData.current.localSettings.values;

    module.exports = {
        readFileSync(filename, options) {
            return values[filename];
        },

        writeFileSync(filename, data) {
            values[filename] = data;
        },

        existsSync(filename) {
            if (values[filename]) {
                return true;
            }
            return false;
        }
    }
});