
function tmp(p)

if nargin == 0
    p = pwd;
end

d = dir(p);

for i=3:length(d)
    f = fullfile(p, d(i).name);
    if isfolder(f)
        tmp(f);
    elseif contains(f, '.js')
        disp(['<script src="' f '"></script>'])
        if ~contains(f, 'browserify')
            s = fileread(f);
            if ~contains(s, 'require((exports, module) => {')
                s = ['require((exports, module) => {' newline s newline '});'];
                writematrix(s,f,'FileType','text','QuoteStrings',false)
            end
        end
    end
end